package com.qianlq.customer.converter;import com.qianlq.customer.model.entity.UserEntity;import com.qianlq.customer.model.dto.UserDto;import org.mapstruct.Mapper;import org.mapstruct.Mapping;import org.mapstruct.Mappings;import org.mapstruct.factory.Mappers;import java.util.List;@Mapper(componentModel = "spring")public interface User2UserDtoMapper {  User2UserDtoMapper INSTANCE = Mappers.getMapper(User2UserDtoMapper.class);  @Mappings({    @Mapping(target = "id", source = "user.id"),    @Mapping(target = "name", source = "user.name"),    @Mapping(target = "birth", source = "user.birth")  })  UserDto user2UserDto(UserEntity user);  List<UserDto> user2UserDto(List<UserEntity> users);}